INSERT INTO inquiry
(inquiry_id, inquiry_title, inquiry_content, inquiry_date , filesrc , member_id , buy_inquiry )
VALUES(
	(SELECT NVL(MAX(TO_NUMBER(inquiry_id)),0)+1 FROM product), #{inquiryTitle, jdbcType=VARCHAR}, #{inquiryContent, jdbcType=VARCHAR} , SYSDATE
	, #{fileSrc, javaType=String, jdbcType=VARCHAR}  , #{memberId, jdbcType=VARCHAR} , #{buyInquiry} 
	)

